import React from 'react';
import '../PostList.css';

function PostList({ isLoggedIn, posts }) {
  // 임시로 만든 데이터
  const currentUser = isLoggedIn ? '로그인한 사용자 아이디' : '익명';

  // 수정 버튼 클릭 시 실행되는 함수
  const handleEdit = (postId) => {
    // 여기에 수정 기능을 구현합니다.
    console.log(`게시물 ${postId}를 수정합니다.`);
  };

  // 삭제 버튼 클릭 시 실행되는 함수
  const handleDelete = (postId) => {
    // 여기에 삭제 기능을 구현합니다.
    console.log(`게시물 ${postId}를 삭제합니다.`);
  };

  return (
    <div className="PostList">
      <div><h1>게시글 </h1></div>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>제목</th>
            <th>내용</th>
            <th>작성자</th>
            <th>작성날짜</th>
            <th>수정</th>
            <th>삭제</th>
          </tr>
        </thead>
        <tbody>
          {/* 임시 데이터를 사용하여 테이블 행을 생성 */}
          {posts.map(post => (
            <tr key={post.id}>
              <td>{post.id}</td>
              <td>{post.title}</td>
              <td>{post.content}</td>
              <td>{isLoggedIn ? currentUser : post.author}</td>
              <td>{post.date}</td>
              <td><button onClick={() => handleEdit(post.id)}>수정</button></td>
              <td><button onClick={() => handleDelete(post.id)}>삭제</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PostList;
