import React, { useState } from 'react';
import '../PostList.css';

function PostList({ isLoggedIn, onLogout }) {
  // 임시로 만든 데이터
  const posts = [
    { id: 1, title: '글 제목 1', content: '글 내용 1', writer:'', date:'' },
    { id: 2, title: '글 제목 2', content: '글 내용 2', writer:'', date:'' },
    { id: 3, title: '글 제목 3', content: '글 내용 3', writer:'', date:'' },
    // 필요한 만큼 데이터 추가
  ];

  const getCurrentTime = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const hour = String(now.getHours()).padStart(2, '0');
    const minute = String(now.getMinutes()).padStart(2, '0');
    const second = String(now.getSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hour}:${minute}:${second}`;
  };

  const handleLogout = () => {
    onLogout();
  };

  return (
    <div className="PostList">
      <div><h1>게시글</h1></div> 
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>제목</th>
            <th>내용</th>
            <th>작성자</th>
            <th>작성날짜</th>
          </tr>
        </thead>
        <tbody>
          {/* 임시 데이터를 사용하여 테이블 행을 생성 */}
          {posts.map(post => (
            <tr key={post.id}>
              <td className="Id">{post.id}</td>
              <td>{post.title}</td>
              <td>{post.content}</td>
              <td>{isLoggedIn ? '로그인한 사용자 아이디' : '익명'}</td>
              <td>{getCurrentTime()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PostList;
