import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../PostList.css';

function PostList({ isLoggedIn }) {
  // ÏûÑÏãúÎ°ú ÎßåÎì† Îç∞Ïù¥ÌÑ∞
  const [posts, setPosts] = useState([
    { id: 1, title: 'Í∏Ä Ï†úÎ™© 1', content: 'Í∏Ä ÎÇ¥Ïö© 111111111111111111111111111111111111111111111111111', writer:'ÌôçÍ∏∏Îèô', date:'', likes: 0, dislikes: 0 },
    { id: 2, title: 'Í∏Ä Ï†úÎ™© 2', content: 'Í∏Ä ÎÇ¥Ïö© 2', writer:'ÌôçÍ∏∏Îèô', date:'', likes: 0, dislikes: 0 },
    { id: 3, title: 'Í∏Ä Ï†úÎ™© 3', content: 'Í∏Ä ÎÇ¥Ïö© 3', writer:'ÌôçÍ∏∏Îèô', date:'', likes: 0, dislikes: 0 },
    // ÌïÑÏöîÌïú ÎßåÌÅº Îç∞Ïù¥ÌÑ∞ Ï∂îÍ∞Ä
  ]);

  const getCurrentTime = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const hour = String(now.getHours()).padStart(2, '0');
    const minute = String(now.getMinutes()).padStart(2, '0');
    const second = String(now.getSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hour}:${minute}:${second}`;
  };

  // ÏàòÏ†ï Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ïã§ÌñâÎêòÎäî Ìï®Ïàò
  const handleEdit = (postId) => {
    if (!isLoggedIn) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    console.log(`Í≤åÏãúÎ¨º ${postId}Î•º ÏàòÏ†ïÌï©ÎãàÎã§.`);
    // Ïó¨Í∏∞Ïóê ÏàòÏ†ï Í∏∞Îä•ÏùÑ Íµ¨ÌòÑÌï† Ïàò ÏûàÏäµÎãàÎã§.
  };

  // ÏÇ≠Ï†ú Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ïã§ÌñâÎêòÎäî Ìï®Ïàò
  const handleDelete = (postId) => {
    if (!isLoggedIn) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    console.log(`Í≤åÏãúÎ¨º ${postId}Î•º ÏÇ≠Ï†úÌï©ÎãàÎã§.`);
    // Ïó¨Í∏∞Ïóê ÏÇ≠Ï†ú Í∏∞Îä•ÏùÑ Íµ¨ÌòÑÌï† Ïàò ÏûàÏäµÎãàÎã§.
  };

  // Ï∂îÏ≤ú Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ïã§ÌñâÎêòÎäî Ìï®Ïàò
  const handleRecommend = (postId) => {
    if (!isLoggedIn) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    setPosts(prevPosts => prevPosts.map(post => {
      if (post.id === postId) {
        return { ...post, likes: post.likes + 1 };
      }
      return post;
    }));
  };

  // ÎπÑÏ∂îÏ≤ú Î≤ÑÌäº ÌÅ¥Î¶≠ Ïãú Ïã§ÌñâÎêòÎäî Ìï®Ïàò
  const handleUnrecommend = (postId) => {
    if (!isLoggedIn) {
      alert('Î°úÍ∑∏Ïù∏ ÌõÑ Ïù¥Ïö© Í∞ÄÎä•Ìï©ÎãàÎã§.');
      return;
    }
    setPosts(prevPosts => prevPosts.map(post => {
      if (post.id === postId) {
        return { ...post, dislikes: post.dislikes + 1 };
      }
      return post;
    }));
  };

  return (
    <div className="PostList">
      <div><h1>Í≤åÏãúÍ∏Ä</h1></div> 
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th className="post_title">Ï†úÎ™©</th>
            <th className="post_cont">ÎÇ¥Ïö©</th>
            <th>ÏûëÏÑ±Ïûê</th>
            <th>ÏûëÏÑ±ÎÇ†Ïßú</th>
            <th>ÏàòÏ†ï/ÏÇ≠Ï†ú</th>
            <th>Ï∂îÏ≤ú/ÎπÑÏ∂îÏ≤ú</th>
          </tr>
        </thead>
        <tbody>
          {/* ÏûÑÏãú Îç∞Ïù¥ÌÑ∞Î•º ÏÇ¨Ïö©ÌïòÏó¨ ÌÖåÏù¥Î∏î ÌñâÏùÑ ÏÉùÏÑ± */}
          {posts.map(post => (
            <tr key={post.id}><Link to={`/detail/${post.id}`}>
              <td className="Center">{post.id}</td>
              <td><Link to={`/detail/${post.id}`}>{post.title}</Link></td>
              <td><Link to={`/detail/${post.id}`}>{post.content}</Link></td>
              <td className="Center">{post.writer}</td>
              <td className="Center">{getCurrentTime()}</td>
              <td className="Center">
                <button onClick={() => handleEdit(post.id)}>ÏàòÏ†ï</button>
                <button onClick={() => handleDelete(post.id)}>ÏÇ≠Ï†ú</button>
              </td>
              <td className="Center">
                <button onClick={() => handleRecommend(post.id)}>Ï∂îÏ≤ú</button> üëç {post.likes}
                <button onClick={() => handleUnrecommend(post.id)}>ÎπÑÏ∂îÏ≤ú</button> üëé {post.dislikes}
              </td>
              </Link></tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default PostList;
