import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import '../Detail.css'; // 파일 경로를 정확히 지정해주세요.

function Detail() {
  // useParams 훅을 사용하여 URL에서 게시물 ID를 추출합니다.
  const { id } = useParams();

  // 임시로 만든 예시 데이터를 가져옵니다.
  const posts = [
    { id: 1, title: '글 제목 1', content: '글 내용 1', writer:'홍길동', date:'' },
    { id: 2, title: '글 제목 2', content: '글 내용 2', writer:'홍길동', date:'' },
    { id: 3, title: '글 제목 3', content: '글 내용 3', writer:'홍길동', date:'' },
    // 필요한 만큼 데이터 추가
  ];

  const getCurrentTime = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    const hour = String(now.getHours()).padStart(2, '0');
    const minute = String(now.getMinutes()).padStart(2, '0');
    const second = String(now.getSeconds()).padStart(2, '0');
    return `${year}-${month}-${day} ${hour}:${minute}:${second}`;
  };

  // 해당 ID에 맞는 게시물을 찾습니다.
  const selectedPost = posts.find(post => post.id === parseInt(id));

  // 선택된 게시물이 없다면 에러 메시지를 표시합니다.
  if (!selectedPost) {
    return <div>해당 게시물을 찾을 수 없습니다.</div>;
  }

  // 댓글 입력을 위한 상태 변수와 핸들러 함수 정의
  const [comment, setComment] = useState('');

  const handleChange = (event) => {
    setComment(event.target.value);
  };

  const handleSubmit = () => {
    console.log('댓글 추가:', comment);
    // 여기에 댓글을 추가하는 로직을 구현할 수 있습니다.
    // 이후에 입력 필드를 초기화하거나 댓글 목록을 갱신할 수 있습니다.
    setComment('');
  };

  return (
    <div className="Detail">
      <div className="Main">
        <h2>게시물 상세 페이지</h2>
        <p>ID: {selectedPost.id}</p>
        <p>제목: {selectedPost.title}</p>
        <p>내용: {selectedPost.content}</p>
        <p>작성자: {selectedPost.writer}</p>
        <p>작성날짜: {getCurrentTime()}</p>
      </div>
      
      {/* 댓글 입력 폼 */}
      <div className="CommentForm">
        <h3>댓글 입력</h3>
        <textarea
          value={comment}
          onChange={handleChange}
          placeholder="댓글을 입력해주세요..."
        />
        <button onClick={handleSubmit}>댓글 등록</button>
      </div>
    </div>
  );
}

export default Detail;
