// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import About from './About';
import Contact from './Contact';
import Login from './Login';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태를 저장하는 상태 변수
  const [username, setUsername] = useState(''); // 사용자 이름을 저장하는 상태 변수
  const [password, setPassword] = useState(''); // 비밀번호를 저장하는 상태 변수

  const handleLogin = (username, password) => {
    setIsLoggedIn(true); // 로그인 상태 변경
    setUsername(username); // 사용자 이름 저장
    setPassword(password); // 비밀번호 저장
  };

  const handleLogout = () => {
    setIsLoggedIn(false); // 로그아웃 상태 변경
    setUsername(''); // 사용자 이름 초기화
    setPassword(''); // 비밀번호 초기화
  };

  return (
    <div className="App">
      <header className="Header">
        <h1>게시판</h1>
        <div className="HeaderButtons">
          {!isLoggedIn ? (
            <Link to="/login" className="LoginButton">로그인</Link>
          ) : (
            <button onClick={handleLogout} className="LogoutButton">로그아웃</button>
          )}
        </div>
      </header>
      {isLoggedIn && ( // 로그인 상태에 따라 메뉴바를 보여줌
        <nav className="Menu">
          <ul>
            <li>
              <Link to="/">홈</Link>
            </li>
            <li>
              <Link to="/about">소개</Link>
            </li>
            <li>
              <Link to="/contact">연락</Link>
            </li>
          </ul>
        </nav>
      )}
      <main className="Content">
        <Routes>
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route
            path="/login"
            element={<Login onLogin={handleLogin} />} // 로그인 성공 시 콜백 함수 전달
          />
        </Routes>
      </main>
      <footer className="Footer">
        <p>&copy; 2024 게시판 앱</p>
      </footer>
    </div>
  );
}

export default App;
