import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useNavigate } from 'react-router-dom';
import About from './about';
import Contact from './contact';
import Login from './Login'; // Login 컴포넌트를 추가로 import합니다.
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogin = () => {
    // 로그인 버튼을 누르면 로그인 상태를 변경합니다.
    setIsLoggedIn(true);
    setIsMenuOpen(true);
  };

  const handleLogout = () => {
    // 로그아웃 버튼을 누르면 로그아웃 상태로 변경합니다.
    setIsLoggedIn(false);
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    // 메뉴 토글 버튼을 누를 때 마다 메뉴 상태를 변경합니다.
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <Router>
      <div className="App">
        <header className="Header">
          <h1>게시판</h1>
        </header>
        {isLoggedIn && (
          <aside className={`Menu ${isMenuOpen ? 'open' : ''}`}>
            <nav>
              <ul>
                <li>
                  <Link to="/" onClick={toggleMenu}>홈</Link>
                </li>
                <li>
                  <Link to="/about" onClick={toggleMenu}>소개</Link>
                </li>
                <li>
                  <Link to="/contact" onClick={toggleMenu}>연락</Link>
                </li>
                <li>
                  <button onClick={handleLogout}>로그아웃</button>
                </li>
              </ul>
            </nav>
          </aside>
        )}
        <main className="Content">
          {/* 로그인 상태에 따라 로그인 폼 또는 로그인 성공 메시지를 표시합니다. */}
          {!isLoggedIn ? (
            <Login onLogin={handleLogin} />
          ) : (
            <h2>로그인 성공! 메뉴를 이용해주세요.</h2>
          )}
          <Routes>
            <Route path="/about" element={<About />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <footer className="Footer">
          <p>&copy; 2024 게시판 앱</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
