import React, { useState, useEffect } from 'react';
import { Container, Form, Button } from 'react-bootstrap';
import { useToasts } from 'react-bootstrap-toasts';
import axios from 'axios';

function Login() {
    const toasts = useToasts();

    const [userId, setUserId] = useState('');
    const [password, setPassword] = useState('');
    const [saveIdCheck, setSaveIdCheck] = useState(false);

    const changeUserId = event => {
        setUserId(event.target.value);
    };

    const changePassword = event => {
        setPassword(event.target.value);
    };

    const SAVED_USER_ID = "savedUserId";

    const doLogin = () => { 
        let tmpPasswd = document.getElementById('inputPassword').value;

        let formData = { userId: userId, password: tmpPasswd };
        axios.post('/doLogin', JSON.stringify(formData), {
            headers: { "Content-Type": `application/json`}
            })
        .then((response) => {
            let reply = response.data;

            toasts.show({
                headerContent: 'Login 결과',
                bodyContent: reply.message,
                toastProps: {
                    bg: 'Light',
                    autohide: true,
                    delay: 3000,
                  },
              });

            if (reply.success) {
                if (saveIdCheck) {
                    localStorage.setItem(SAVED_USER_ID, userId);
                } else {
                    localStorage.removeItem(SAVED_USER_ID);
                }
            }
        })
        .catch((error) => {
            console.log(error);
        });
    };

    useEffect(() => { 
        let savedId = localStorage.getItem(SAVED_USER_ID);
        if (savedId != null) {
            setSaveIdCheck(true);
            setUserId(savedId);
        }
    }, []);

    return (
        <Container>
            <Form>
                <br /><br />
                <Form.Group className="mb-3" controlId="formGroupEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="Enter email" value={userId} onChange={changeUserId} />
                </Form.Group>
                <br />
                <Form.Group className="mb-3" controlId="formGroupPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" value={password} id='inputPassword' onChange={changePassword} />
                </Form.Group>
                <br />
                <Form.Check type='checkbox' label='save email address' checked={saveIdCheck} onChange={() => setSaveIdCheck(!saveIdCheck)} />
                <br />
                <Button variant="primary" onClick={doLogin}>로그인</Button>
            </Form>
        </Container>
    );
}

export default Login;
