// App.js
import React, { useState } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
    setIsMenuOpen(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsMenuOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <div className="App">
      <header className="Header">
        <h1>게시판</h1>
      </header>
      {isLoggedIn && (
        <aside className={`Menu ${isMenuOpen ? 'open' : ''}`}>

            <ul>
              <li>
                <a href="/" onClick={toggleMenu}>홈</a>
              </li>
              <li>
                <a href="/about" onClick={toggleMenu}>소개</a>
              </li>
              <li>
                <a href="/contact" onClick={toggleMenu}>연락</a>
              </li>
              <li>
                <button onClick={handleLogout}>로그아웃</button>
              </li>
            </ul>

        </aside>
      )}
      <main className="Content">
        {!isLoggedIn ? (
          <div className="Login">
            <h2>로그인</h2>
            <button onClick={handleLogin}>로그인</button>
          </div>
        ) : (
          <h2>로그인 성공! 메뉴를 이용해주세요.</h2>
        )}
      </main>
      <footer className="Footer">
        <p>&copy; 2024 게시판 앱</p>
      </footer>
    </div>
  );
}

export default App;
