// app.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Home from './pages/Home'; // Home 컴포넌트 import
import Login from './pages/Login'; // Login 컴포넌트 import
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // 로그인 상태를 저장하는 상태 변수

  const handleLogin = () => {
    setIsLoggedIn(true); // 로그인 상태 변경
  };

  const handleLogout = () => {
    setIsLoggedIn(false); // 로그아웃 상태 변경
  };

  return (
    <div className="App">
      <Routes>
        <Route path="/" element={<Home isLoggedIn={isLoggedIn} />} /> {/* Home 페이지 */}
        <Route
          path="/login"
          element={<Login onLogin={handleLogin} />}
        />
      </Routes>
    </div>
  );
}

export default App;
