import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from 'react-router-dom';
import { Container, Form } from 'react-bootstrap';
import About from './about';
import Contact from './contact';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    if (!username && !password) {
      alert('아이디와 비밀번호를 입력해주세요.');
    } else if (!username) {
      alert('아이디를 입력해주세요.');
    } else if (!password) {
      alert('비밀번호를 입력해주세요.');
    } else {
      setIsLoggedIn(true);
      setIsMenuOpen(true);
    }
  };

  const handleLogout = () => {
    setUsername('');
    setPassword('');
    setIsLoggedIn(false);
    setIsMenuOpen(false);
    // 페이지를 새로고침하여 메뉴 클릭 시 이전의 메시지를 사라지게 함
    window.location.reload();
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleUsernameKeyDown = (e) => {
    if (e.key === 'Enter') {
      document.getElementById('passwordInput').focus();
    }
  };

  const handlePasswordKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleLogin();
    }
  };

  return (
    <div className="App">
      <header className="Header">
        <h1>게시판</h1>
      </header>
      {isLoggedIn && (
        <aside className={`Menu ${isMenuOpen ? 'open' : ''}`}>
          <nav>
            <ul>
              <li>
                <Link to="/" onClick={() => setIsMenuOpen(false)}>홈</Link>
              </li>
              <li>
                <Link to="/about" onClick={() => setIsMenuOpen(false)}>소개</Link>
              </li>
              <li>
                <Link to="/contact" onClick={() => setIsMenuOpen(false)}>연락</Link>
              </li>
              <li>
                <button onClick={handleLogout}>로그아웃</button>
              </li>
            </ul>
          </nav>
        </aside>
      )}
      <main className="Content">
        {!isLoggedIn ? (
          <div className="LoginForm">
            <Container>
              <Form>
                <br /><br />
                <Form.Group className="mb-3" controlId="formGroupEmail">
                  <Form.Label>아이디</Form.Label>
                  <Form.Control
                    type="text"
                    placeholder="아이디"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    onKeyDown={handleUsernameKeyDown}
                  />
                </Form.Group>
                <br />
                <Form.Group className="mb-3" controlId="formGroupPassword">
                  <Form.Label>비밀번호</Form.Label>
                  <Form.Control
                    id="passwordInput"
                    type="password"
                    placeholder="비밀번호"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    onKeyDown={handlePasswordKeyDown}
                  />
                </Form.Group>
                <br />
                <button onClick={handleLogin}>로그인</button>
              </Form>
            </Container>
          </div>
        ) : (
          <Navigate to="/" replace={true} /> // 로그인 성공 시 메인 페이지로 이동하여 페이지를 다시 렌더링
        )}
        <Routes>
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
        </Routes>
      </main>
      <footer className="Footer">
        <p>&copy; 2024 게시판 앱</p>
      </footer>
    </div>
  );
}

export default App;
