import { useState } from 'react';
import { Container, Form } from 'react-bootstrap';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // 로그인 처리 로직
    setIsLoggedIn(true);
  };

  return (
    <div className="App">
      <header className="Header">
        <h1>게시판</h1>
      </header>
      <div className="Content">
        {!isLoggedIn ? (
          <div className="LoginForm">
            <Container>
              <Form>
                <br /><br />
                <Form.Group className="mb-3" controlId="formGroupEmail">
                  <Form.Label>아이디</Form.Label>
                  <Form.Control type="text" placeholder="아이디" value={username} onChange={(e) => setUsername(e.target.value)} />
                </Form.Group>
                <br />
                <Form.Group className="mb-3" controlId="formGroupPassword">
                  <Form.Label>비밀번호</Form.Label>
                  <Form.Control type="password" placeholder="비밀번호" value={password} onChange={(e) => setPassword(e.target.value)} />
                </Form.Group>
                <br />
                <button onClick={handleLogin}>로그인</button>
              </Form>
            </Container>
          </div>
        ) : (
          <div className="LoginSuccessMessage">
            <h2>로그인 성공! 메뉴를 이용해주세요.</h2>
          </div>
        )}
      </div>
      <footer className="Footer">
        <h2>위지언</h2>
      </footer>
    </div>
  );
}

export default App;
